SET SQL_MODE = "NO_AUTO_VALUE_ON_ZERO";
SET time_zone = "+00:00";

DROP TABLE IF EXISTS `ads`;

CREATE TABLE IF NOT EXISTS `ads` (
  `medRec1` text NOT NULL,
  `rec1Status` tinyint(1) NOT NULL,
  `medRec2` text NOT NULL,
  `rec2Status` tinyint(1) NOT NULL,
  `medRec3` text NOT NULL,
  `rec3Status` tinyint(1) NOT NULL
) ENGINE=MyISAM DEFAULT CHARSET=latin1;

TRUNCATE TABLE `ads`;


DROP TABLE IF EXISTS `cacheSettings`;

CREATE TABLE IF NOT EXISTS `cacheSettings` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `recentCacheEnable` tinyint(4) NOT NULL,
  `recentCacheExpireTime` int(11) NOT NULL,
  `categoryCacheEnable` tinyint(4) NOT NULL,
  `categoryCacheExpireTime` int(11) NOT NULL,
  `tagCacheEnable` tinyint(4) NOT NULL,
  `tagCacheExpireTime` int(11) NOT NULL,
  `topCacheEnable` tinyint(4) NOT NULL,
  `topCacheExpireTime` int(11) NOT NULL,
  `productCacheEnable` tinyint(4) NOT NULL,
  `productCacheExpireTime` int(11) NOT NULL,
  `sidebarCacheEnable` tinyint(4) NOT NULL,
  `sidebarCacheExpireTime` int(11) NOT NULL,
  `relatedCacheEnable` tinyint(4) NOT NULL,
  `relatedCacheExpireTime` int(11) NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 AUTO_INCREMENT=2 ;

TRUNCATE TABLE `cacheSettings`;


DROP TABLE IF EXISTS `captcha`;

CREATE TABLE IF NOT EXISTS `captcha` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `onOffAdminCaptcha` int(2) NOT NULL,
  `onOffContactCaptcha` int(2) NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 AUTO_INCREMENT=2 ;

TRUNCATE TABLE `captcha`;


DROP TABLE IF EXISTS `categories`;

CREATE TABLE IF NOT EXISTS `categories` (
  `parentId` int(11) NOT NULL DEFAULT '0',
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `permalink` varchar(65) CHARACTER SET latin1 NOT NULL,
  `name` varchar(30) CHARACTER SET latin1 NOT NULL,
  `displayOrder` int(1) NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci AUTO_INCREMENT=234 ;

TRUNCATE TABLE `categories`;


DROP TABLE IF EXISTS `comments`;

CREATE TABLE IF NOT EXISTS `comments` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `disqusStatus` int(11) NOT NULL,
  `disqusName` varchar(30) NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 AUTO_INCREMENT=2 ;

TRUNCATE TABLE `comments`;


DROP TABLE IF EXISTS `currencySettings`;

CREATE TABLE IF NOT EXISTS `currencySettings` (
  `currencySymbol` varchar(100) NOT NULL,
  `priceInDollar` double NOT NULL,
  `showBefore` int(11) NOT NULL,
  `id` int(11) NOT NULL AUTO_INCREMENT,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 AUTO_INCREMENT=3 ;

TRUNCATE TABLE `currencySettings`;


DROP TABLE IF EXISTS `envatoSources`;

CREATE TABLE IF NOT EXISTS `envatoSources` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `websiteName` varchar(100) NOT NULL,
  `websiteCategory` varchar(100) NOT NULL,
  `cid` int(11) NOT NULL,
  `posts` int(11) NOT NULL DEFAULT '0',
  `updated` datetime NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM  DEFAULT CHARSET=latin1 AUTO_INCREMENT=158 ;

TRUNCATE TABLE `envatoSources`;


DROP TABLE IF EXISTS `hotProducts`;

CREATE TABLE IF NOT EXISTS `hotProducts` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `permalink` varchar(100) NOT NULL,
  `todayClicks` int(11) NOT NULL,
  `weeklyClicks` int(11) NOT NULL,
  `monthlyClicks` int(11) NOT NULL,
  `alltimeClicks` int(11) NOT NULL,
  `date` varchar(30) NOT NULL,
  `weekUpdateDate` date NOT NULL,
  `monthUpdateDate` date NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB  DEFAULT CHARSET=latin1 AUTO_INCREMENT=708 ;

TRUNCATE TABLE `hotProducts`;


DROP TABLE IF EXISTS `mediaSettings`;

CREATE TABLE IF NOT EXISTS `mediaSettings` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `limitPosts` int(11) NOT NULL,
  `enablePossible` tinyint(4) NOT NULL,
  `enableSidebar` int(1) NOT NULL,
  `indexThumbnail` varchar(255) COLLATE utf8_unicode_ci NOT NULL,
  `sidebarThumbnail` varchar(255) COLLATE utf8_unicode_ci NOT NULL,
  `sidebarProLimit` int(11) NOT NULL,
  `possibleThumbnail` varchar(255) COLLATE utf8_unicode_ci NOT NULL,
  `possibleProLimit` int(11) NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci AUTO_INCREMENT=2 ;

TRUNCATE TABLE `mediaSettings`;


DROP TABLE IF EXISTS `pages`;

CREATE TABLE IF NOT EXISTS `pages` (
  `id` tinyint(4) NOT NULL AUTO_INCREMENT,
  `permalink` varchar(80) NOT NULL,
  `title` varchar(70) NOT NULL,
  `content` text NOT NULL,
  `status` tinyint(1) NOT NULL,
  `displayOrder` tinyint(4) NOT NULL,
  `keywords` varchar(255) NOT NULL,
  `description` varchar(500) NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM  DEFAULT CHARSET=latin1 AUTO_INCREMENT=63 ;

TRUNCATE TABLE `pages`;


DROP TABLE IF EXISTS `products`;

CREATE TABLE IF NOT EXISTS `products` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `cid` int(11) NOT NULL,
  `publishDate` varchar(100) NOT NULL,
  `updateDate` varchar(200) NOT NULL,
  `permalink` varchar(70) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,
  `title` varchar(253) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,
  `description` text CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,
  `screens` varchar(253) NOT NULL,
  `image` varchar(253) NOT NULL,
  `url` varchar(253) NOT NULL,
  `demo` varchar(253) NOT NULL,
  `price` int(11) NOT NULL,
  `tags` varchar(250) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,
  `clicks` int(11) NOT NULL,
  `pageViews` int(11) NOT NULL,
  `uniqHits` int(11) NOT NULL,
  PRIMARY KEY (`id`),
  KEY `cid` (`cid`)
) ENGINE=MyISAM  DEFAULT CHARSET=latin1 AUTO_INCREMENT=711 ;

TRUNCATE TABLE `products`; 


DROP TABLE IF EXISTS `rssSettings`;

CREATE TABLE IF NOT EXISTS `rssSettings` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `rssEnable` tinyint(1) NOT NULL,
  `limitRss` int(11) NOT NULL,
  `descLength` int(11) NOT NULL,
  `catRssEnable` tinyint(1) NOT NULL,
  `tagRssEnable` tinyint(4) NOT NULL,
  `topRssEnable` tinyint(4) NOT NULL,
  `recentRssEnable` tinyint(4) NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM  DEFAULT CHARSET=latin1 AUTO_INCREMENT=2 ;

TRUNCATE TABLE `rssSettings`;


DROP TABLE IF EXISTS `settings`;

CREATE TABLE IF NOT EXISTS `settings` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `title` varchar(70) NOT NULL,
  `websiteName` varchar(70) NOT NULL,
  `description` varchar(160) NOT NULL,
  `metaTags` varchar(160) NOT NULL,
  `envato` varchar(50) NOT NULL,
  `username` varchar(25) NOT NULL,
  `password` varchar(32) NOT NULL,
  `email` varchar(254) NOT NULL,
  `rootpath` varchar(254) NOT NULL,
  `logo` varchar(255) NOT NULL,
  `favicon` varchar(255) NOT NULL,
  `version` float NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM  DEFAULT CHARSET=latin1 AUTO_INCREMENT=2 ;

TRUNCATE TABLE `settings`;


DROP TABLE IF EXISTS `sitemaps`;

CREATE TABLE IF NOT EXISTS `sitemaps` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `catsStatus` tinyint(1) NOT NULL,
  `pagesStatus` tinyint(1) NOT NULL,
  `contFormStatus` tinyint(1) NOT NULL,
  `postsStatus` tinyint(1) NOT NULL,
  `outputPath` varchar(50) NOT NULL,
  `lastModified` datetime NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM  DEFAULT CHARSET=latin1 AUTO_INCREMENT=2 ;

TRUNCATE TABLE `sitemaps`;


DROP TABLE IF EXISTS `socialProfiles`;

CREATE TABLE IF NOT EXISTS `socialProfiles` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `facebook` varchar(50) NOT NULL,
  `twitter` varchar(50) NOT NULL,
  `google` varchar(50) NOT NULL,
  `pinterest` varchar(50) NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM  DEFAULT CHARSET=latin1 AUTO_INCREMENT=2 ;

TRUNCATE TABLE `socialProfiles`;


DROP TABLE IF EXISTS `stats`;
 
CREATE TABLE IF NOT EXISTS `stats` (
  `pageViews` int(11) NOT NULL,
  `uniqueHits` int(11) NOT NULL,
  `datetime` date NOT NULL,
  `clicks` int(11) NOT NULL 
) ENGINE=MyISAM DEFAULT CHARSET=latin1; 

ALTER TABLE `socialProfiles` ADD `status` INT NOT NULL DEFAULT '1' ;

DROP TABLE `license`;

ALTER TABLE `mediaSettings` ADD `categoriesLimit` INT NOT NULL AFTER `id`;

UPDATE `mediaSettings` SET `categoriesLimit` = '7';

ALTER TABLE `products` ADD `rating` VARCHAR(200) NOT NULL AFTER `tags`;

UPDATE `settings` SET `version` = 2.3;